import React, { useState, useRef, useEffect } from 'react'
import Swal from 'sweetalert2';
import axios from 'axios'
import Select from 'react-select'
function Add({ users, setUsers, setIsAdding }) {
    const [salary, setSalary] = useState('');
    const [loan_amnt, setloan_amnt] = useState('');
    const [term, setterm] = useState('');
    const [empl_length, setemp_length] = useState('');
    const [home_ownersh, sethome_ownersh] = useState(['RENT', 'OWN', 'MORTGAGE', 'OTHER', 'NONE', 'ANY']);
    const [purpose, setpurpose] = useState('');
    const [city, setcity] = useState('');
    const [dti, setdti] = useState('');
    const [delinq, setdelinq] = useState('');
    const [revol, setrevol] = useState('');
    const [totalacc, settotalacc] = useState('');
    const [prevcreditlength, setprevcreditlength] = useState('');
    const [visible, setVisible] = React.useState(false);
    const [confidence, setconfidence] = useState('');
    const [loanverified, setloanverified] = useState('');
    const [interest_rate, setinterest_rate] = useState('');
    const textInput = useRef(null);

    const home_ownership_options = [
        { value: 'MORTGAGE', label: 'MORTGAGE' },
        { value: 'RENT', label: 'RENT' },
        { value: 'OTHER', label: 'OTHER' },
        { value: 'NONE', label: 'NONE' },
        { value: 'ANY', label: 'ANY' },
        { value: 'OWN', label: 'OWN' }
    ]
    const handleChange = (e) => {
        sethome_ownersh(e.target.value);
    };


    useEffect(() => {
        textInput.current.focus();
    }, [])
    const handlePredict = async(e) => {
        e.preventDefault();
        const id = users.length + 1;
        console.log('home ownership is '+home_ownersh);
        const result=await axios.get("http://localhost:8081/predictLoan",{
            params: {
                'annual_inc':salary,
                'loan_amnt':loan_amnt,
                'term':term,
                'emp_length':empl_length,
                'home_ownership':home_ownersh,
                'purpose':purpose,
                'addr_state':city,
                'dti':dti,
                'delinq_2yrs':delinq,
                'revol_util':revol,
                'total_acc':totalacc,
                'longest_credit_length':prevcreditlength,
                'verification_status':"verified"
            }
        });
        console.log(result);
        let data=result['data']
        setconfidence(data['confidence']);
        setloanverified(data['prediction']);
        setinterest_rate(data['interest_rate']);
        console.log(result['status'])
        if(result['status']===200){
            console.log('Inside status');
            setVisible(true);
        }



        console.log(confidence,loanverified,interest_rate);

        /*Swal.fire({
            icon: "success",
            title:"Results",
            text: `${result}`, 
            showConfirmButton: false
            //timer:15000

        })*/
    }
    const handleSave=async(e) => {
        const id = users.length + 1;
        if(confidence==='' || loanverified==='') {
            Swal.fire({
                icon: "error",
                title: "Results",
                text: `Kindly predict the case before submitting.`,
                showConfirmButton: true,
                //timer: 1500

            })
        }else {
            let params= {
                'annual_inc': salary,
                    'loan_amnt': loan_amnt,
                    'term': term,
                    'emp_length': empl_length,
                    'home_ownership': home_ownersh,
                    'purpose': purpose,
                    'addr_state': city,
                    'dti': dti,
                    'delinq_2yrs': delinq,
                    'revol_util': revol,
                    'total_acc': totalacc,
                    'longest_credit_length': prevcreditlength,
                    'verification_status': "verified",
                    'confidence': confidence,
                    'prediction': loanverified,
                    'interest_rate': interest_rate
            }
            const result=  await axios.post("http://localhost:8081/saveCase", params);
            console.log(result);
            if(result['data']===-1){
                Swal.fire({
                    icon: "error",
                    title:"Results",
                    text: `Error occurred while saving the record!`,
                    showConfirmButton: true,
                    //timer:1500

                })
            }else {
                Swal.fire({
                    icon: "success",
                    title: "Results",
                    text: `Case ID : `+result['data'],
                    showConfirmButton: true,
                    //timer:1500

                })
            }
        }
        //setUsers(users);
        //setIsAdding(false);


    }
    return (
        <div className="small-container">
            <form onSubmit={handlePredict}>
                <h1>Predict Case</h1>
                <label htmlFor="salary">Salary ($)</label>
                <input
                    id="salary"
                    type="number"
                    name="salary"
                    ref={textInput}
                    value={salary}
                    onChange={e => setSalary(e.target.value)}
                />
                <label htmlFor="loan_amnt">Loan amount</label>
                <input
                    id="loan_amnt"
                    type="loan_amnt"
                    name="loan_amnt"
                    value={loan_amnt}
                    onChange={e => setloan_amnt(e.target.value)}
                />
                <label htmlFor="term">Term</label>
                <input
                    id="term"
                    type="term"
                    name="term"
                    value={term}
                    onChange={e => setterm(e.target.value)}
                />
                <label htmlFor="empl_length">Employee Length</label>
                <input
                    id="empl_length"
                    type="empl_length"
                    name="empl_length"
                    value={empl_length}
                    onChange={e => setemp_length(e.target.value)}
                />
                <label htmlFor="home_ownersh">Housing status</label>

                <Select
                    options={home_ownership_options}
                    //id="home_ownersh"
                    //type="home_ownersh"
                    //name="home_ownersh"
                    value={home_ownersh}
                    onChange={handleChange}
                />
                <label htmlFor="purpose">Purpose</label>
                <input
                    id="purpose"
                    type="purpose"
                    name="purpose"
                    value={purpose}
                    onChange={e => setpurpose(e.target.value)}
                />
                <label htmlFor="city">City</label>
                <input
                    id="city"
                    type="city"
                    name="city"
                    value={city}
                    onChange={e => setcity(e.target.value)}
                />
                <label htmlFor="dti">DTI</label>
                <input
                    id="dti"
                    type="dti"
                    name="dti"
                    value={dti}
                    onChange={e => setdti(e.target.value)}
                />
                <label htmlFor="delinq">delinq_2yrs</label>
                <input
                    id="delinq"
                    type="delinq"
                    name="delinq"
                    value={delinq}
                    onChange={e => setdelinq(e.target.value)}
                />
                <label htmlFor="revol">Revol_util</label>
                <input
                    id="revol"
                    type="revol"
                    name="revol"
                    value={revol}
                    onChange={e => setrevol(e.target.value)}
                />
                <label htmlFor="totalacc">Total accounts</label>
                <input
                    id="totalacc"
                    type="totalacc"
                    name="totalacc"
                    value={totalacc}
                    onChange={e => settotalacc(e.target.value)}
                />
                <label htmlFor="prevcreditlength">Age of oldest active account</label>
                <input
                    id="prevcreditlength"
                    type="prevcreditlength"
                    name="prevcreditlength"
                    value={prevcreditlength}
                    onChange={e => setprevcreditlength(e.target.value)}
                />
                <div style={{display: visible ? 'block' : 'none'}}>
                <label htmlFor="confidence">Confidence</label>
                <input
                    disabled={true}
                    id="confidence"
                    type="confidence"
                    name="confidence"
                    value={confidence}
                    //onChange={e => setconfidence(e.target.value)}
                />
                <label htmlFor="loanverified">Loan Verification Status</label>
                <input
                    disabled={true}
                    id="loanverified"
                    type="loanverified"
                    name="loanverified"
                    value={loanverified}
                    //onChange={e => setloanverified(e.target.value)}
                />
                <label htmlFor="interest_rate">Predicted Interest Rate</label>
                <input
                    disabled={true}
                    id="interest_rate"
                    type="interest_rate"
                    name="interest_rate"
                    value={interest_rate}
                    //onChange={e => setinterest_rate(e.target.value)}
                />
                </div>
                <div style={{ marginTop: '30px' }}>
                    <input type="submit" value="Predict" />
                    <input
                        style={{ marginLeft: '12px' }}
                        className="muted-button"
                        type="button"
                        value="Save"
                        onClick={handleSave}
                    />
                    <input
                        style={{ marginLeft: '12px' }}
                        className="muted-button"
                        type="button"
                        value="Cancel"
                        onClick={() => setIsAdding(false)}
                    />

                </div>

            </form>
        </div>
    )
}

export default Add
