import React, { useState, useRef, useEffect } from 'react'
import Swal from 'sweetalert2';
function Add({users,setUsers,setIsAdding}) {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [salary, setSalary] = useState('');
    const [date, setDate] = useState('');
    const [loan_status, setloan] = useState('');
    const [loan_amnt,setloan_amnt]=useState('');
    const [term,setterm]=useState('');
    const [empl_length,setemp_length]=useState('');
    const [home_ownersh,sethome_ownersh]=useState('');
    const [purpose,setpurpose]=useState('');
    const [city,setcity]=useState('');
    const [dti,setdti]=useState('');
    const [delinq,setdelinq]=useState('');
    const [revil,setrevol]=useState('');
    const [totalacc,settotal_Acc]=useState('');
    const [creditlength,setcreditlength]=useState('');
    const textInput = useRef(null);
    useEffect(() => {
        textInput.current.focus();
    }, [])
    const handleAdd = e => {
        e.preventDefault();
        if (!firstName || !lastName || !email || !salary || !date) {
            return Swal.fire({
                icon: 'error',
                title: 'Error!',
                text: 'All fields are required.',
                showConfirmButton: true
            });
        }

        const id = users.length + 1;
        const newEmployee = {
            id,
            firstName,
            lastName,
            email,
            salary,
            date,
            loan_status
        }
        users.push(newEmployee);
        setUsers(users);
        setIsAdding(false);

        Swal.fire({
            icon: 'success',
            title: 'Added!',
            text: `${firstName} ${lastName}'s data has been Added.`,
            showConfirmButton: false,
            timer: 1500
        });
    }
  return (
    <div className="small-container">
            <form onSubmit={handleAdd}>
                <h1>Add User</h1>
                <label htmlFor="firstName">First Name</label>
                <input
                    id="firstName"
                    type="text"
                    ref={textInput}
                    name="firstName"
                    value={firstName}
                    onChange={e => setFirstName(e.target.value)}
                />
                <label htmlFor="lastName">Last Name</label>
                <input
                    id="lastName"
                    type="text"
                    name="lastName"
                    value={lastName}
                    onChange={e => setLastName(e.target.value)}
                />
                <label htmlFor="email">Email</label>
                <input
                    id="email"
                    type="email"
                    name="email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                />
                <label htmlFor="salary">Salary ($)</label>
                <input
                    id="salary"
                    type="number"
                    name="salary"
                    value={salary}
                    onChange={e => setSalary(e.target.value)}
                />
                <label htmlFor="date">Date</label>
                <input
                    id="date"
                    type="date"
                    name="date"
                    value={date}
                    onChange={e => setDate(e.target.value)}
                />
                <div style={{ marginTop: '30px' }}>
                    <input type="submit" value="Add" />
                    <input
                        style={{ marginLeft: '12px' }}
                        className="muted-button"
                        type="button"
                        value="Cancel"
                        onClick={() => setIsAdding(false)}
                    />
                </div>
            </form>
        </div>
  )
}

export default Add
